mapper.select:
SELECT

FROM task_doc_log
WHERE tenant_id = #{tenantId,jdbcType=OTHER,typeHandler=UUIDTypeHandler}
AND document_id = #{documentId,jdbcType=OTHER,typeHandler=UUIDTypeHandler}
AND phase = #{phase,jdbcType=VARCHAR}

mapper.insert:

    SELECT max(retry_time) FROM task_doc_log WHERE
    tenant_id = #{tenantId,jdbcType=OTHER,typeHandler=UUIDTypeHandler}
    AND document_id = #{documentId,jdbcType=OTHER,typeHandler=UUIDTypeHandler}
    AND task_doc_log.phase = #{phase,jdbcType=VARCHAR}

INSERT INTO task_doc_log (tenant_id,
document_id, phase, retry_time,
action_result, message, action_timestamp
) VALUES (#{tenantId,jdbcType=OTHER,typeHandler=UUIDTypeHandler},
#{documentId,jdbcType=OTHER,typeHandler=UUIDTypeHandler}, #{phase,jdbcType=VARCHAR},
(SELECT CASE
WHEN max(retry_time) IS NULL
THEN 1
ELSE max(retry_time) + 1
END
FROM task_doc_log
WHERE tenant_id = #{tenantId,jdbcType=OTHER,typeHandler=UUIDTypeHandler}
AND document_id = #{documentId,jdbcType=OTHER,typeHandler=UUIDTypeHandler}
AND task_doc_log.phase = #{phase,jdbcType=VARCHAR})
, #{actionResult,jdbcType=VARCHAR}, #{message,jdbcType=VARCHAR}, #{actionTimestamp, jdbcType=TIMESTAMP})
